import React, {Component} from 'react';

function Todo({ todo }) {
  return (
    <div className="todo">
      {todo.text}
    </div>
  );
};

function App() {
  const [todos, setTodos] = React.useState([
    { text: "Code" },
    { text: "Eat" },
    { text: "Learn" },
    { text: "Sleep" }
  ]);

  return (
    <div className="app">
      <div className="todo-list">
        { todos.map((todo, index) => (
          <Todo 
          key = {index}
          index
          />
        ))}
      </div>
    </div>
  );  
}

export default App;
