import React, {Component} from 'react';

function TodoForm(){

}

function Todo({ todo, index, complete, removeTodo }) {
  return (
    <div className="list-group-item" style={{ textDecoration: todo.isCompleted ? "line-through" : "" }}>
      {todo.text}
      <div>
        <button onClick={() => complete(index)}>Complete</button>
      </div>
    </div>
  );
};

function App() {
  const [todos, setTodos] = React.useState([
    { text: "Code"  },
    { text: "Eat"   },
    { text: "Learn" },
    { text: "Sleep" }
  ]);

  return (
    <div className="app">
      <div className="list-group">
        { todos.map((todo, index) => (
          <Todo 
          key = {index}
          index = {index}
          todo = {todo}
          />
        ))}
      </div>
    </div>
  );  
}

export default App;
