import React from 'react';

function Item({title, index, completeTodo, removeItem}) {
    return (
        <div className="item">
            <div className="input-group">
                <p className="list-group-item" style={{ textDecoration: completeTodo.isCompleted ? "line-through" : "" }}>
                    {title}
                </p>
                <button className="btn btn-primary" onClick={() => completeTodo(index)}>
                    Complete
                </button>
            </div>
        </div>
    )
}

export default Item;
