import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends Component {

  formatName(user) {
    return user.fname + ' ' + user.lname;
  }

  user = {
    fname: "Bishal",
    lname: "Magar"
  }; 

  render(){
    const element = <h1>Hello, {formatName(user)}</h1>

    ReactDOM.render(
    element,
    document.getElementById('root')
    );
  }
}

export default App;
