import React from 'react';

function Item({title, index, completeTodo}) {
    return (
        <div className="item">
            <div className="input-group">
                <span className="list-group-item" style={{ textDecoration: title.isCompleted ? "line-through" : "" }}>
                    {title} 
                    <button className="btn btn-danger" onClick={() => completeTodo(index)}>
                        Complete
                    </button>
                </span>
            </div>
        </div>
    )
}

export default Item;
