import React, {Component} from 'react';

class App extends Component {

  constructor(props){
    super(props);

    this.state = {
      data: []
    }
  }

    addTodo(value){
      const todo = {text: value, id: window.id++}
      this.state.data.push(todo);
      this.setState({data: this.state.data});
  }

    delTodo(id){
      const remainder = this.state.data.filter((todo) => {
        if (todo.id !== id) return todo;
      });
    }

  render(){
    return (
      <div>
        <main className="container">
          <h4 className="display-4">Todo List</h4>
          <hr className="my-4"></hr>
          <form class="form-inline">
            <div className='form-group mb-2'>
              <label for="todo" className="sr-only">Todo</label>
              <input type="text" readOnly className="form-control-plaintext" id="todo" value="Title"></input>
            </div>
            <div className="form-group mx-sm-3 mb-2">
              <label for="todo-title" className="sr-only">Title</label>
              <input type="text" className="form-control" id="todo-title" placeholder="Enter here..."></input>
            </div>
            <button type="submit" className="btn btn-primary mb-2">Submit</button>
          </form>
          <hr className="my-4"></hr>
          <div className="card" style={{ width: 550 }}>
            <ul className="list-group list-group-flush">
              <li className="list-group-item">Code</li>
              <li className="list-group-item">Eat</li>
              <li className="list-group-item">Sleep</li>
            </ul>
          </div>
        </main>
      </div>
    );
  }
}

export default App;
