import React from 'react';
import '/App.css'

function TodoForm({ addTodo }){
  const [value, setValue] = React.useState("");

  const handleSubmit = e => {
    e.preventDefault();
    if (!value) return;
    addTodo(value);
    setValue("");
  };

  return (
    <form onSubmit = {handleSubmit}>
      <input type="text" className="form-control" placeholder="Add Here..." value={ value } onChange={e => setValue(e.target.value)}/>
    </form>
  );
}

function Todo({ todo, index, complete, removeTodo }) {
  return (
    <div className="list-group-item" style={{ textDecoration: todo.isCompleted ? "line-through" : "none" }}>
      {todo.text}
      <div>
        <button onClick={() => complete(index)}>Completed</button>
        <button onClick={() => removeTodo(index)}>Remove</button>
      </div>
    </div>
  );
};

function App() {
  const [todos, setTodos] = React.useState([]);

  const addTodo = text => {
    const newTodos = [...todos, { text }];
    setTodos(newTodos)
  };

  const completeTodo = index => {
    const newTodos = [...todos];
    newTodos[index].isCompleted = true;
    setTodos(newTodos);
  };

  const removeTodo = index => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  return (
    <div className="app">
      <TodoForm addTodo={addTodo} />
      <div className="list-group">
        { todos.map((todo, index) => (
          <Todo 
          key = {index}
          index = {index}
          todo = {todo}
          complete = {completeTodo}
          removeTodo = {removeTodo}
          />
        ))}
      </div>
    </div>
  );  
}

export default App;
