import React from 'react';
import './App.css';

function TodoForm({ addTodo }){
  const [value, setValue] = React.useState("");

  const handleSubmit = e => {
    e.preventDefault();
    if (!value) return;
    addTodo(value);
    setValue("");
  };

  return (
      <form className="form-inline" onSubmit={handleSubmit}>
        <div className="form-group mb-2">
          <label for="todo" className="sr-only">Todo</label>
          <input type="text" readOnly className="form-control-plaintext" value="Title" id="todo" />
        </div>
        <div className="form-group mx-sm-3 mb-2">
          <label for="title" className="sr-only">Todo Title</label>
          <input type="text" className="form-control" id="title" value={value} onChange={e => setValue(e.target.value)} placeholder="Enter here..." />
        </div>
      </form>
  );
}


function App() {
  const [todos, setTodos] = React.useState([]);

  const addTodo = text => {
    const newTodos = [...todos, { text }];
    setTodos(newTodos);
  };

  const completeTodo = index => {
    const newTodos = [...todos];
    newTodos[index].isCompleted = true;
    setTodos(newTodos);
  };

  const removeTodo = index => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  return (
    <div className="container">
      <TodoForm addTodo={addTodo} />
      <div className="list-group">
        { todos.map((todo, index) => (
          <Todo 
          key = {index}
          index = {index}
          todo = {todo}
          complete = {completeTodo}
          removeTodo = {removeTodo}
          />
        ))}
      </div>
    </div>
  );  
}

export default App;
