import React, { useState } from 'react';
import './App.css';
import Form from './Form';
import Item from './Item';

function App() {

    const [todos, setTodo] = useState(["Eat", "Sleep"]);

    return (
        <div className="app">
            <div className="container-xs bg-ligth">
                <Form />
                <ul>
                    {
                    todos.map(todo => (
                        <Item title={todo} />
                    )) 
                    }
                </ul>
            </div>
        </div>
    )
}

export default App;
