import './App.css';
import Form from './Form';
import Item from './Item';

function App() {

    const [todos, setTodo] = useState([
        {title: "Eat"},
        {title: "Code"},
        {title: "Sleep"},
    ]);
    const addTodo = text => {
        const newTodos = [...todos, {todo}];
        setTodo(newTodos);
    }

    return (
        <div className="app">
            <div className="container bg-light">
                <Form title={addTodo} />
                <ul className="list-group">
                    {
                    todos.map(todo => (
                        <Item title={todo.title} />
                    )) 
                    }
                </ul>
            </div>
        </div>
    )
}

export default App;
